@st.cache
def get_sp500_list():
    url = "https://stockmarketmba.com/stocksinthesp500.php"
    html = requests.get(url).content
    sp500_list = pd.read_html(html)
    sp500 = sp500_list[-1]
    sp500 = sp500['Symbol'].to_list()
    sp500.pop(-1)
    return sp500.Symbol

