def hightlight_var(val):
    isOne = val == 1
    return ['background: lightgray' if cell else '' for cell in isOne]

def hightlight_diag(df):
    a = np.full(df.shape, '', dtype='<U24')
    np.fill_diagonal(a, 'background: lightgray')
    return pd.DataFrame(a, index=df.index, columns=df.columns)

